CREATE TABLE IF NOT EXISTS data_source ( id String, title String, url String, description String ) ENGINE = MergeTree PRIMARY KEY (id)
CREATE TABLE IF NOT EXISTS orgunit ( uid String, name String, level UInt16, sourceid String, lastupdated DateTime ) ENGINE = ReplacingMergeTree(lastupdated) ORDER BY (uid, name, level, sourceid)
CREATE TABLE IF NOT EXISTS category ( categoryoptioncomboid String, categoryoptioncomboname String, sourceid String, lastupdated DateTime ) ENGINE = ReplacingMergeTree(lastupdated) ORDER BY (categoryoptioncomboid, sourceid)
CREATE TABLE IF NOT EXISTS dataelement ( uid String, name String, sourceid String, lastupdated DateTime ) ENGINE = ReplacingMergeTree(lastupdated) ORDER BY (uid, sourceid)